import React from "react";

type TestimonialProps = {
    text: string
    rating: number
};

/**
 * This represent a single testimonial card with a text description and 
 * customer rating
 * 
 * @param param
 * @returns 
 */
const Testimonial: React.FC<TestimonialProps> = ({ children, text, rating }) => (
    <React.Fragment>

            <div className="ol p-5 flex flex-col items-center justify-start w-96">    
                <p className="font-sans text-2xl leading-10 italic text-left text-black-500">
                    { text }
                </p>
                <div className="ol flex flex-row items-center justify-between">
                {
                    [...Array(rating).keys()].map( () => {
                        return (
                            <span>*</span>
                        )
                    })
                }
                </div>
            </div>

    </React.Fragment>
);

export default Testimonial;
