                        flex flex-row lg:flex-col w-full h-full items-center justify-around lg:w-full 
                        lg:bg-black  lg:pt-0 ">

            <hr />
            <div id="logo">
                {/* Display logo with copyright */}
                <img className="ol flex-1" src="/assets/images/t.png" alt="" />

            </div>

            <div id="links">
                {/* Categorized columns of links */}
                Show links here
            </div>

            <div id="newsletter">
                {/* Text field to capture email to join newsletter */}
                Textfield for newsletter
                <input type="text" >

                </input>
            </div>

        </div>





    </React.Fragment>
);

