import React from "react";

type TestimonialProps = {
    text: string
    rating: number
};

/**
 * This represent a single testimonial card with a text description and 
 * customer rating
 * 
 * @param param
 * @returns 
 */
const Testimonial: React.FC<TestimonialProps> = ({ children, text, rating }) => (
    <React.Fragment>
            <div className="testimonial p-5 flex flex-col items-center justify-start w-96">    
                <p className="description">
                    { text }
                </p>
                <div className="ol flex flex-row items-center justify-between">
                    <ul className="ol flex flex-row items-center justify-between w-full">
                        {
                            [...Array(rating).keys()].map( () => {
                                return (
                                    <li className="fa fa-star">*</li>
                                )
                            })
                        }
                    </ul>
                </div>
            </div>
    </React.Fragment>
);

export default Testimonial;

